import React from 'react'
import './assets/css/base/base.css'

import Home from './paginas/Home.jsx'
import Sobre from './paginas/Sobre.jsx'

function App() {

  function router() {
    const location = window.location.pathName

    if (location === '/sobre') {
      return <Sobre />
    } else {
      return <Home />
    }
  }

  return (
    <>
      {router}
    </>
  )
}

export default App
